function switchTheme(){var e=document.getElementById("theme-button"),t=document.getElementsByTagName("html")[0];t.classList.toggle("dark"),"DAY"==e.innerHTML?(e.innerHTML="NIGHT",setCookie("theme","night",5184e6)):(e.innerHTML="DAY",setCookie("theme","day",5184e6))}function setCookie(e,t,n){var i=new Date;i.setTime(i.getTime()+n);var o="expires="+i.toGMTString();document.cookie=e+"="+t+";"+o+";path=/"}function getCookie(e){for(var t=e+"=",n=document.cookie.split(";"),i=0;i<n.length;i++){var o=n[i].trim();if(0===o.indexOf(t))return o.substring(t.length,o.length)}return""}"night"==getCookie("theme")&&switchTheme();var button=document.getElementById("theme-button");button.addEventListener("click",switchTheme);